// Utilities
  import {
    mapMutations,
    mapState
  } from 'vuex'

  export default {
    props: {
      opened: {
        type: Boolean,
        default: false
      }
    },
    data: () => ({
      links: [
        {
          to: '/',
          icon: 'mdi-view-dashboard',
          text: 'Início'
        },
        {
          to: '/batalhoes',
          icon: 'mdi-clipboard-outline',
          text: 'Batalhões'
        },
        {
          to: '/escalas',
          icon: 'mdi-scale',
          text: 'Escalas'
        },
        {
          to: '/exames',
          icon: 'mdi-file-document',
          text: 'Exames'
        },
        {
          to: '/hierarquias',
          icon: 'mdi-chart-bubble',
          text: 'Hierarquia'
        },
        {
          to: '/pessoas',
          icon: 'mdi-account-multiple',
          text: 'Pessoas'
        },
        {
          to: '/setores',
          icon: 'mdi-map-marker',
          text: 'Setores'
        },
        {
          to: '/notifications',
          icon: 'mdi-settings',
          text: 'Configurações'
        }
      ]
    }),

    computed: {
      ...mapState('app', ['image', 'color']),
      inputValue: {
        get () {
          return this.$store.state.app.drawer
        },
        set (val) {
          this.setDrawer(val)
        }
      }
    },

    methods: {
      ...mapMutations('app', ['setDrawer', 'toggleDrawer'])
    }
  }
